import { Avatar } from "@material-ui/core";
import { FolderOpen, PermContactCalendar } from "@material-ui/icons";
import React,{useState} from "react";
import { Link } from "react-router-dom";
import { useLocalContext } from "../../context/context";
import "./style.css";
const JoinedClasses = ({ classData }) => {
  const { dataInfo } = useLocalContext();
  const [classDetail, setClassDetail] = useLocalContext();
  const detail=useState('');

  const handleClick= async () =>{
    setClassDetail(classData.id)
    console.log("classDetail: ", classDetail)
  }

  return (
    <li className="joined__list">
      <div className="joined__wrapper">
        <div className="joined__container">
          <div className="joined__imgWrapper">
          </div>
          <div className="joined__image" />
          <div className="joined__content">
            <Link className="joined__title" onClick={handleClick(detail=classData)} to={`/${classData.id}`}>
              <h2>{classData.name}</h2>
              <p>{classData.description}</p>
            </Link>
            {classData.owner.map((item)=>(
              <p className="joined__owner">
                {item.id!==dataInfo.id ? item.username :null}
              </p>
            ))}
          </div>
        </div>
        <Avatar
          className="joined__avatar"
          src="../../img/photo.png"
        />
      </div>
      <div className="joined__bottom">
        <PermContactCalendar />
        <FolderOpen />
      </div>
    </li>
  );
};

export default JoinedClasses;
