import React,{useState,useEffect} from 'react';
import { useLocalContext } from "../../context/context";
import "./style.css";

export default function ClassDetail() {
  const {classDetail} = useLocalContext();
  const { dataInfo } = useLocalContext();

  const [code,setCode]=useState();
  
  useEffect(() => {
  const _code= classDetail.owner.map((item)=>{
    if (item.id===dataInfo.id) {setCode(classDetail.code)} 
    return(
      <div className="bottom">
        <h3>CODE</h3>
        {
          (code) 
          ? <h3>{code}</h3>
          :null
        }
      </div>
    )
  })},[code]);

                
  return (
    <div className="list">
    <div className="wrapper">
      <div className="container">
        <div className="image" />
        <div className="content">
          <div className="title">
            <h1>{classDetail.name}</h1>
            <h3>{classDetail.description}</h3>
          </div>
        </div>
        </div>
      </div>
      <div>{ }</div>
      <div className="bottom">
        {
              classDetail.owner.map((item)=>(
                <h3>
                  {item.id===dataInfo.id ? classDetail.code :null}
                </h3>
              ))
          }
        </div>
    </div>
  );
}
